        return (
      
    <div className="sidebar-container">
  <div className="sidebar-logo">

    ESI-Scolarité
  </div>
  <ul className="sidebar-navigation">
  

  <li className="header"><strong><center> {localStorage.getItem("nom")} : {localStorage.getItem("prenom")} </center></strong><center>{localStorage.getItem("login")}</center>   
  <center><strong>(étudiant - Groupe : {localStorage.getItem("groupe")} )</strong></center>
  </li>
    <li className="header">Fonctionnalités</li>
   

    <li>
      <a href="/etud">
        <i className="fa fa-users" aria-hidden="true"></i> Mes absences
      </a>
    </li>

    <li>
      <a href="/compte">
        <i class="fa fa-tachometer" aria-hidden="true"></i> Gérer mon compte
      </a>
    </li>
    <li className="header"></li>
    
   
    <li>
      <a href="/deconnexion">
        <i className="fa fa-info-circle" aria-hidden="true"></i> Déconnexion
      </a>
    </li>
  </ul>
</div>
)
}
}
